import React, { useEffect, useState } from "react";
import "../Styles/Style.css";

const API_URL = "http://localhost:5000/api/feedback";

const Feedback = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    name: "",
    rating: 0,
    comment: "",
  });

  // Fetch feedback on load
  useEffect(() => {
    fetchFeedback();
  }, []);

  const fetchFeedback = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setFeedbacks(data);
    } catch (err) {
      console.error("Failed to load feedback");
    }
  };

  const submitFeedback = async (e) => {
    e.preventDefault();
    if (!form.name || !form.rating || !form.comment) {
      alert("Please fill all fields");
      return;
    }

    setLoading(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const newFeedback = await res.json();
      setFeedbacks([newFeedback, ...feedbacks]);
      setForm({ name: "", rating: 0, comment: "" });
    } catch (err) {
      alert("Something went wrong");
    }

    setLoading(false);
  };

  return (
    <section className="feedback-section">
      <h2 className="feedback-title">What People Say About Us</h2>
      <p className="feedback-subtitle">
        Real feedback from real users ⭐
      </p>

      <form onSubmit={submitFeedback} className="feedback-form">
        <input
          className="feedback-input"
          placeholder="Your Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />

        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${form.rating >= star ? "active" : ""}`}
              onClick={() => setForm({ ...form, rating: star })}
            >
              ★
            </span>
          ))}
        </div>

        <textarea
          className="feedback-textarea"
          placeholder="Write your feedback..."
          value={form.comment}
          onChange={(e) => setForm({ ...form, comment: e.target.value })}
        />

        <button className="feedback-button" disabled={loading}>
          {loading ? "Submitting..." : "Submit Feedback"}
        </button>
      </form>

      <div className="feedback-list">
        {feedbacks.length === 0 && (
          <p className="feedback-empty">No feedback yet.</p>
        )}

        {feedbacks.map((item) => (
          <div key={item._id} className="feedback-card">
            <div className="feedback-header">
              <strong>{item.name}</strong>
              <div className="feedback-rating">
                {"★".repeat(item.rating)}
                {"☆".repeat(5 - item.rating)}
              </div>
            </div>
            <p className="feedback-comment">{item.comment}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Feedback;
