// src/pages/Dashboard.jsx
import React from 'react';

function Dashboard() {
  const handleTestAPI = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      const data = await response.json();
      alert(`Backend Status: ${data.status}\nMessage: ${data.message}`);
    } catch (error) {
      alert('Backend connection failed. Make sure backend is running on port 5000.');
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      textAlign: 'center',
      padding: '20px',
      paddingTop:'100px'
    }}>
      <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>SkyReti Educore</h1>
      <p style={{ fontSize: '1.5rem', marginBottom: '2rem' }}>Premium Education Management Platform</p>
      
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '15px',
        padding: '30px',
        maxWidth: '600px',
        margin: '20px'
      }}>
        <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>System Status</h2>
        
        <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', marginBottom: '30px' }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{
              width: '80px',
              height: '80px',
              background: '#10b981',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 10px',
              fontSize: '2rem'
            }}>✓</div>
            <p>Frontend Running</p>
            <p style={{ fontSize: '0.9rem', opacity: 0.8 }}>Port 3000</p>
          </div>
          
          <div style={{ textAlign: 'center' }}>
            <div style={{
              width: '80px',
              height: '80px',
              background: '#3b82f6',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 10px',
              fontSize: '2rem',
              cursor: 'pointer'
            }} onClick={handleTestAPI}>⚡</div>
            <p>Backend API</p>
            <p style={{ fontSize: '0.9rem', opacity: 0.8 }}>Port 5000</p>
          </div>
        </div>
        
        <button 
          onClick={handleTestAPI}
          style={{
            background: 'white',
            color: '#667eea',
            border: 'none',
            padding: '12px 30px',
            borderRadius: '8px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            marginBottom: '20px'
          }}
        >
          Test Backend Connection
        </button>
        
        <p style={{ fontSize: '0.9rem', opacity: 0.8 }}>
          Click the blue circle or button to test backend connection
        </p>
      </div>
      
      <div style={{ marginTop: '40px' }}>
        <h3 style={{ marginBottom: '20px' }}>Available Solutions</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '15px' }}>
          {['School Management', 'College Management', 'Library System', 'HR Payroll', 'Analytics'].map((item, idx) => (
            <div key={idx} style={{
              background: 'rgba(255,255,255,0.1)',
              padding: '15px 25px',
              borderRadius: '8px',
              minWidth: '150px'
            }}>
              {item}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
