// src/components/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, 
         XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useInView } from 'react-intersection-observer';
import 'aos/dist/aos.css';
import AOS from 'aos';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [scrollProgress, setScrollProgress] = useState(0);
  const [loading, setLoading] = useState(true);
  
  // Initialize AOS
  useEffect(() => {
    AOS.init({
      duration: 800,
      easing: 'ease-in-out',
      once: true,
      mirror: false,
      disable: window.innerWidth < 768
    });
    AOS.refresh();
    
    // Simulate loading
    setTimeout(() => setLoading(false), 1500);
    
    // Scroll progress
    const handleScroll = () => {
      const totalHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = (window.scrollY / totalHeight) * 100;
      setScrollProgress(progress);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  
  // Data for charts
  const performanceData = [
    { month: 'Jan', users: 4000, revenue: 2400 },
    { month: 'Feb', users: 3000, revenue: 1398 },
    { month: 'Mar', users: 2000, revenue: 9800 },
    { month: 'Apr', users: 2780, revenue: 3908 },
    { month: 'May', users: 1890, revenue: 4800 },
    { month: 'Jun', users: 2390, revenue: 3800 },
  ];
  
  const pieData = [
    { name: 'Students', value: 400, color: '#667eea' },
    { name: 'Teachers', value: 300, color: '#764ba2' },
    { name: 'Admins', value: 200, color: '#10b981' },
    { name: 'Guests', value: 100, color: '#f59e0b' },
  ];
  
  // Stats data
  const stats = [
    { 
      id: 1, 
      value: '12,847', 
      label: 'Total Users', 
      change: '+24%', 
      positive: true,
      icon: 'üë•',
      description: 'Active platform users'
    },
    { 
      id: 2, 
      value: '342', 
      label: 'Courses', 
      change: '+15%', 
      positive: true,
      icon: 'üìö',
      description: 'Published courses'
    },
    { 
      id: 3, 
      value: '94.7%', 
      label: 'Satisfaction', 
      change: '+3.2%', 
      positive: true,
      icon: '‚≠ê',
      description: 'User satisfaction rate'
    },
    { 
      id: 4, 
      value: '2.4K', 
      label: 'Engagement', 
      change: '-1.5%', 
      positive: false,
      icon: 'üî•',
      description: 'Daily active engagement'
    },
  ];
  
  // Quick actions
  const quickActions = [
    { id: 1, label: 'Create Course', icon: '‚ûï', color: 'var(--accent)' },
    { id: 2, label: 'Analytics Report', icon: 'üìä', color: 'var(--info)' },
    { id: 3, label: 'User Management', icon: 'üë•', color: 'var(--primary)' },
    { id: 4, label: 'System Settings', icon: '‚öôÔ∏è', color: 'var(--warning)' },
  ];
  
  // Recent activity
  const activities = [
    { id: 1, title: 'New course published', time: '5 minutes ago', icon: 'üìö' },
    { id: 2, title: 'User registration completed', time: '15 minutes ago', icon: 'üë§' },
    { id: 3, title: 'System update applied', time: '1 hour ago', icon: 'üîÑ' },
    { id: 4, title: 'Monthly report generated', time: '2 hours ago', icon: 'üìà' },
    { id: 5, title: 'Performance optimization', time: '3 hours ago', icon: '‚ö°' },
  ];
  
  // Intersection Observer
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });
  
  return (
    <div className="dashboard-container">
      {/* Scroll Progress */}
      <div 
        className="scroll-progress" 
        style={{ width: `${scrollProgress}%` }}
      />
      
      {/* Dashboard Header */}
      <header 
        className="dashboard-header"
        data-aos="fade-down"
        data-aos-delay="100"
      >
        <h1 className="dashboard-title">SkyReti Dashboard</h1>
        <p className="dashboard-subtitle">
          Real-time insights and analytics for your education platform
        </p>
      </header>
      
      {/* Performance Indicators Grid */}
      <div className="performance-grid" ref={ref}>
        {stats.map((stat, index) => (
          <div
            key={stat.id}
            className="glass-card"
            data-aos="fade-up"
            data-aos-delay={index * 100}
          >
            <div className="card-icon">
              {loading ? (
                <div className="loading-shimmer" style={{ width: '40px', height: '40px' }} />
              ) : (
                stat.icon
              )}
            </div>
            <div className="card-value">
              {loading ? (
                <div className="loading-shimmer" style={{ width: '120px', height: '50px', margin: '0 auto' }} />
              ) : (
                stat.value
              )}
            </div>
            <div className="card-label">{stat.label}</div>
            <p className="card-description" style={{ 
              color: 'var(--text-tertiary)', 
              fontSize: '0.9rem', 
              textAlign: 'center',
              marginTop: 'var(--spacing-xs)' 
            }}>
              {stat.description}
            </p>
            <div className={`performance-indicator ${stat.positive ? 'indicator-positive' : 'indicator-negative'}`}>
              <span>{stat.change}</span>
              <span>{stat.positive ? '‚Üó' : '‚Üò'}</span>
            </div>
          </div>
        ))}
      </div>
      
      {/* Charts Section */}
      <div className="charts-section">
        {/* Performance Chart */}
        <div 
          className="glass-card chart-container"
          data-aos="fade-right"
          data-aos-delay="200"
        >
          <h3 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
            Performance Overview
          </h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={performanceData}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis dataKey="month" stroke="var(--text-secondary)" />
              <YAxis stroke="var(--text-secondary)" />
              <Tooltip 
                contentStyle={{ 
                  background: 'var(--glass-bg)', 
                  backdropFilter: 'var(--glass-blur)',
                  border: '1px solid var(--glass-border)',
                  borderRadius: '12px'
                }}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="users" 
                stroke="#667eea" 
                strokeWidth={3}
                dot={{ r: 4 }}
                activeDot={{ r: 6 }}
              />
              <Line 
                type="monotone" 
                dataKey="revenue" 
                stroke="#10b981" 
                strokeWidth={3}
                dot={{ r: 4 }}
                activeDot={{ r: 6 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
        
        {/* User Distribution */}
        <div 
          className="glass-card chart-container"
          data-aos="fade-left"
          data-aos-delay="300"
        >
          <h3 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
            User Distribution
          </h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={pieData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                contentStyle={{ 
                  background: 'var(--glass-bg)', 
                  backdropFilter: 'var(--glass-blur)',
                  border: '1px solid var(--glass-border)',
                  borderRadius: '12px'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>
      
      {/* Quick Actions */}
      <div className="quick-actions-section">
        {quickActions.map((action, index) => (
          <button
            key={action.id}
            className="action-button"
            data-aos="zoom-in"
            data-aos-delay={index * 100 + 400}
            style={{ '--button-color': action.color } || {}}
            onClick={() => alert(`Action: ${action.label}`)}
          >
            <span style={{ fontSize: '1.3rem' }}>{action.icon}</span>
            <span>{action.label}</span>
          </button>
        ))}
      </div>
      
      {/* Activity Feed */}
      <div 
        className="glass-card activity-feed"
        data-aos="fade-up"
        data-aos-delay="500"
      >
        <h3 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
          Recent Activity
        </h3>
        <div className="activity-list">
          {activities.map((activity, index) => (
            <div
              key={activity.id}
              className="activity-item"
              data-aos="fade-up"
              data-aos-delay={index * 100 + 600}
            >
              <div className="activity-icon">{activity.icon}</div>
              <div style={{ flex: 1 }}>
                <div style={{ 
                  color: 'var(--text-primary)', 
                  fontWeight: '600',
                  marginBottom: '4px'
                }}>
                  {activity.title}
                </div>
                <div style={{ color: 'var(--text-tertiary)', fontSize: '0.9rem' }}>
                  {activity.time}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
