// src/pages/Solutions.jsx
import React from 'react';
import Layout from '../components/Layout';
import StandardPage from '../components/templates/StandardPage';
import GlassCard from '../components/common/GlassCard';
import { 
  School, 
  GraduationCap, 
  Library, 
  Users, 
  Rocket, 
  Cpu,
  TrendingUp,
  Briefcase 
} from 'lucide-react';

const Solutions = () => {
  const solutions = [
    {
      icon: <School size={32} />,
      title: 'For Schools',
      description: 'Complete school management system with attendance, grading, and parent communication tools.',
      features: ['Attendance Tracking', 'Grade Management', 'Parent Portal']
    },
    {
      icon: <GraduationCap size={32} />,
      title: 'For Universities',
      description: 'Advanced learning management system for higher education institutions.',
      features: ['Course Management', 'Research Tools', 'Alumni Network']
    },
    {
      icon: <Library size={32} />,
      title: 'For Libraries',
      description: 'Digital library management with AI-powered recommendation system.',
      features: ['Digital Catalog', 'AI Recommendations', 'E-book Management']
    },
    {
      icon: <Users size={32} />,
      title: 'For HR Teams',
      description: 'Employee training and development platform with performance tracking.',
      features: ['Training Modules', 'Performance Analytics', 'Skill Development']
    },
    {
      icon: <Rocket size={32} />,
      title: 'For Startups',
      description: 'Scalable platform for growing startups to manage their learning programs.',
      features: ['Scalable Infrastructure', 'Cost-effective', 'Rapid Deployment']
    },
    {
      icon: <Cpu size={32} />,
      title: 'For AI Labs',
      description: 'Advanced AI research and development platform with collaboration tools.',
      features: ['AI Model Training', 'Collaboration Tools', 'Data Management']
    },
    {
      icon: <TrendingUp size={32} />,
      title: 'For Influencers',
      description: 'Content creation and audience engagement platform for educators.',
      features: ['Content Creation', 'Audience Analytics', 'Monetization Tools']
    },
    {
      icon: <Briefcase size={32} />,
      title: 'For Entrepreneurs',
      description: 'Business education and mentorship platform for aspiring entrepreneurs.',
      features: ['Business Courses', 'Mentorship Network', 'Funding Resources']
    }
  ];

  return (
    <Layout>
      <StandardPage
        title="Our Solutions"
        subtitle="Discover comprehensive solutions tailored for every educational need and organization type"
      >
        <div className="grid grid-3" style={{ marginTop: 'var(--spacing-xl)' }}>
          {solutions.map((solution, index) => (
            <div
              key={solution.title}
              data-aos="fade-up"
              data-aos-delay={index * 100}
            >
              <GlassCard
                icon={solution.icon}
                title={solution.title}
                description={solution.description}
                footer={
                  <div style={{ marginTop: 'var(--spacing-md)' }}>
                    <div style={{
                      display: 'flex',
                      flexWrap: 'wrap',
                      gap: '8px'
                    }}>
                      {solution.features.map((feature, idx) => (
                        <span
                          key={idx}
                          style={{
                            padding: '4px 12px',
                            background: 'rgba(255, 255, 255, 0.1)',
                            borderRadius: '20px',
                            fontSize: '0.85rem',
                            color: 'var(--text-secondary)'
                          }}
                        >
                          {feature}
                        </span>
                      ))}
                    </div>
                    <button className="btn btn-secondary" style={{ marginTop: '16px' }}>
                      Learn More â†’
                    </button>
                  </div>
                }
              />
            </div>
          ))}
        </div>
      </StandardPage>
    </Layout>
  );
};

export default Solutions;
