// src/components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  Home, 
  Grid, 
  Zap, 
  DollarSign, 
  BookOpen, 
  LogIn,
  LogOut,
  User,
  Menu,
  X,
  ChevronDown,
  Dashboard
} from 'lucide-react';
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSolutionsOpen, setIsSolutionsOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userName, setUserName] = useState('');

  // Check authentication on mount
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const user = localStorage.getItem('userName');
    if (token) {
      setIsAuthenticated(true);
      setUserName(user || 'User');
    }
  }, []);

  const navItems = [
    { name: 'Home', path: '/', icon: <Home size={18} /> },
    { 
      name: 'Solutions', 
      path: '#',
      icon: <Grid size={18} />,
      dropdown: [
        { name: 'For Schools', path: '/solutions/schools' },
        { name: 'For Universities', path: '/solutions/universities' },
        { name: 'For Libraries', path: '/solutions/libraries' },
        { name: 'For HR Teams', path: '/solutions/hrteams' },
        { name: 'For Startups', path: '/solutions/startups' },
        { name: 'For AI Labs', path: '/solutions/ailabs' },
        { name: 'For Influencers', path: '/solutions/influencers' },
        { name: 'For Entrepreneurs', path: '/solutions/entrepreneurs' },
      ]
    },
    { name: 'Features', path: '/features', icon: <Zap size={18} /> },
    { name: 'Pricing', path: '/pricing', icon: <DollarSign size={18} /> },
    { name: 'Resources', path: '/resources', icon: <BookOpen size={18} /> },
  ];

  // Add dashboard only if authenticated
  if (isAuthenticated) {
    navItems.push({ 
      name: 'Dashboard', 
      path: '/dashboard', 
      icon: <Dashboard size={18} />, 
      highlight: true 
    });
  }

  const handleNavigation = (path) => {
    if (path === '#') return; // Don't navigate for dropdown toggle
    
    navigate(path);
    setIsMenuOpen(false);
    setIsSolutionsOpen(false);
  };

  const handleLogin = () => {
    navigate('/login');
    setIsMenuOpen(false);
  };

  const handleSignup = () => {
    navigate('/signup');
    setIsMenuOpen(false);
  };

  const handleLogout = () => {
    // Clear authentication
    localStorage.removeItem('authToken');
    localStorage.removeItem('userName');
    setIsAuthenticated(false);
    setUserName('');
    navigate('/');
    setIsMenuOpen(false);
  };

  const handleProfile = () => {
    navigate('/profile');
    setIsMenuOpen(false);
  };

  // Check if current path is active
  const isActive = (path) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-content">
          {/* Logo */}
          <Link to="/" className="logo-link">
            <div className="logo-icon">
              <span style={{color: 'white', fontWeight: 'bold', fontSize: '1.25rem'}}>SE</span>
            </div>
            <div>
              <div className="logo-text">SkyReti Educore</div>
              <div className="logo-subtext">Premium Platform</div>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="desktop-nav">
            {navItems.map((item) => (
              <div key={item.name} className="nav-item">
                {item.dropdown ? (
                  <button
                    onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                    className={`nav-button nav-button-default ${isActive(item.path) ? 'nav-button-active' : ''}`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                    <ChevronDown size={16} style={{
                      transition: 'transform 0.2s',
                      transform: isSolutionsOpen ? 'rotate(180deg)' : 'rotate(0deg)'
                    }} />
                  </button>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    className={`nav-button ${item.highlight ? 'nav-button-highlight' : 'nav-button-default'} ${isActive(item.path) && !item.highlight ? 'nav-button-active' : ''}`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}

                {/* Dropdown Menu */}
                {item.dropdown && isSolutionsOpen && (
                  <div className="dropdown-menu">
                    {item.dropdown.map((subItem) => (
                      <button
                        key={subItem.name}
                        onClick={() => handleNavigation(subItem.path)}
                        className="dropdown-item"
                      >
                        <div className="dropdown-indicator"></div>
                        <span>{subItem.name}</span>
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ))}

            {/* Auth Buttons */}
            <div className="auth-buttons">
              {isAuthenticated ? (
                <div className="user-menu" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <div style={{
                      width: '36px',
                      height: '36px',
                      background: 'linear-gradient(135deg, #667eea, #764ba2)',
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontSize: '0.9rem',
                      fontWeight: 'bold'
                    }}>
                      {userName.charAt(0).toUpperCase()}
                    </div>
                    <span style={{ color: '#d1d5db', fontSize: '0.9rem' }}>
                      Hi, {userName.split(' ')[0]}
                    </span>
                  </div>
                  <button
                    onClick={handleProfile}
                    className="sign-in-button"
                    style={{ padding: '0.5rem 1rem' }}
                  >
                    <User size={16} />
                    <span>Profile</span>
                  </button>
                  <button
                    onClick={handleLogout}
                    className="get-started-button"
                    style={{ 
                      background: 'linear-gradient(135deg, #ef4444, #dc2626)',
                      padding: '0.5rem 1rem'
                    }}
                  >
                    <LogOut size={16} />
                    <span>Logout</span>
                  </button>
                </div>
              ) : (
                <>
                  <button
                    onClick={handleLogin}
                    className="sign-in-button"
                  >
                    <LogIn size={18} />
                    <span>Sign In</span>
                  </button>
                  <button
                    onClick={handleSignup}
                    className="get-started-button"
                  >
                    Get Started Free
                  </button>
                </>
              )}
            </div>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="mobile-menu-button"
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="mobile-menu">
            {navItems.map((item) => (
              <div key={item.name} className="mobile-menu-item">
                {item.dropdown ? (
                  <div>
                    <button
                      onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                      className="dropdown-item"
                      style={{width: '100%', justifyContent: 'space-between'}}
                    >
                      <div style={{display: 'flex', alignItems: 'center', gap: '0.75rem'}}>
                        {item.icon}
                        <span>{item.name}</span>
                      </div>
                      <ChevronDown size={16} style={{
                        transition: 'transform 0.2s',
                        transform: isSolutionsOpen ? 'rotate(180deg)' : 'rotate(0deg)'
                      }} />
                    </button>
                    {isSolutionsOpen && (
                      <div className="mobile-menu-dropdown">
                        {item.dropdown.map((subItem) => (
                          <button
                            key={subItem.name}
                            onClick={() => handleNavigation(subItem.path)}
                            className="dropdown-item"
                            style={{justifyContent: 'flex-start'}}
                          >
                            <div className="dropdown-indicator"></div>
                            <span>{subItem.name}</span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    className={`dropdown-item ${item.highlight ? 'nav-button-highlight' : ''} ${isActive(item.path) && !item.highlight ? 'nav-button-active' : ''}`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}
              </div>
            ))}

            <div className="mobile-auth-section">
              {isAuthenticated ? (
                <>
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.75rem',
                    padding: '1rem',
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '0.5rem',
                    marginBottom: '0.5rem'
                  }}>
                    <div style={{
                      width: '40px',
                      height: '40px',
                      background: 'linear-gradient(135deg, #667eea, #764ba2)',
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontWeight: 'bold'
                    }}>
                      {userName.charAt(0).toUpperCase()}
                    </div>
                    <div>
                      <div style={{ fontWeight: '600' }}>{userName}</div>
                      <div style={{ fontSize: '0.9rem', color: '#9ca3af' }}>Premium User</div>
                    </div>
                  </div>
                  <button
                    onClick={handleProfile}
                    className="dropdown-item"
                    style={{justifyContent: 'center', marginBottom: '0.5rem'}}
                  >
                    <User size={18} />
                    <span>Profile</span>
                  </button>
                  <button
                    onClick={handleLogout}
                    className="mobile-get-started-button"
                    style={{ background: 'linear-gradient(135deg, #ef4444, #dc2626)' }}
                  >
                    <LogOut size={18} />
                    <span>Logout</span>
                  </button>
                </>
              ) : (
                <>
                  <button
                    onClick={handleLogin}
                    className="dropdown-item"
                    style={{justifyContent: 'center', marginBottom: '0.5rem'}}
                  >
                    <LogIn size={18} />
                    <span>Sign In</span>
                  </button>
                  <button
                    onClick={handleSignup}
                    className="mobile-get-started-button"
                  >
                    Get Started Free
                  </button>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
