// src/components/Footer.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import "../tyles/style.css";

const Footer = () => {
  return (
    <footer style={{
      background: 'var(--glass-bg)',
      backdropFilter: 'var(--glass-blur)',
      borderTop: '1px solid var(--glass-border)',
      marginTop: 'var(--spacing-xxl)',
      padding: 'var(--spacing-xl) 0',
      position: 'relative',
      zIndex: 'var(--z-base)'
    }}>
      <div className="content-container">
        <div className="grid grid-4">
          <div>
            <h3 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
              SkyReti Educore
            </h3>
            <p style={{ color: 'var(--text-secondary)' }}>
              The world's most advanced AI-powered education platform.
            </p>
          </div>
          
          <div>
            <h4 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
              Solutions
            </h4>
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {['For Schools', 'For Universities', 'For Startups'].map((item) => (
                <li key={item} style={{ marginBottom: '8px' }}>
                  <Link 
                    to={`/solutions/${item.toLowerCase().replace(' ', '')}`}
                    style={{ color: 'var(--text-secondary)', textDecoration: 'none' }}
                  >
                    {item}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
          
          <div>
            <h4 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
              Company
            </h4>
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {['About', 'Careers', 'Contact'].map((item) => (
                <li key={item} style={{ marginBottom: '8px' }}>
                  <a 
                    href={`#${item.toLowerCase()}`}
                    style={{ color: 'var(--text-secondary)', textDecoration: 'none' }}
                  >
                    {item}
                  </a>
                </li>
              ))}
            </ul>
          </div>
          
          <div>
            <h4 style={{ color: 'var(--text-primary)', marginBottom: 'var(--spacing-md)' }}>
              Get Started
            </h4>
            <button className="btn btn-primary btn-lg" style={{ width: '100%' }}>
              Start Free Trial
            </button>
          </div>
        </div>
        
        <div style={{
          marginTop: 'var(--spacing-xl)',
          paddingTop: 'var(--spacing-md)',
          borderTop: '1px solid var(--glass-border)',
          textAlign: 'center',
          color: 'var(--text-tertiary)'
        }}>
          <p>Â© 2024 SkyReti Educore. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
