import React from 'react';
import { Link } from 'react-router-dom';
import {
  Mail,
  Phone,
  MapPin,
  Linkedin,
  Twitter,
  Instagram,
  Github
} from 'lucide-react';
import '../Styles/Style.css';

const Footer = () => {
  return (
    <footer style={footer}>
      <div className="content-container">

        {/* ================= MAIN GRID ================= */}
        <div style={grid}>

          {/* BRAND */}
          <div>
            <h3 style={title}>SkyReti Educore</h3>
            <p style={text}>
              AI-powered digital solutions for Schools, Universities &
              EdTech organizations worldwide.
            </p>
          </div>

          {/* SOLUTIONS */}
          <div>
            <h4 style={title}>Solutions</h4>
            <ul style={ul}>
              <li><Link to="/solutions/schools" style={link}>For Schools</Link></li>
              <li><Link to="/solutions/universities" style={link}>For Universities</Link></li>
              <li><Link to="/solutions/startups" style={link}>For Startups</Link></li>
            </ul>
          </div>

          {/* CONTACT */}
          <div>
            <h4 style={title}>Contact</h4>

            <div style={contactItem}>
              <Mail size={16} />
              <a href="mailto:contact@skyreti.com" style={link}>
                contact@skyreti.com
              </a>
            </div>

            <div style={contactItem}>
              <Phone size={16} />
              <a href="tel:+919999999999" style={link}>
                +91 99999 99999
              </a>
            </div>

            <div style={contactItem}>
              <MapPin size={16} />
              <span style={text}>India • Global Remote</span>
            </div>
          </div>

          {/* SOCIAL + CTA */}
          <div>
            <h4 style={title}>Connect</h4>

            <div style={socialRow}>
              <Social href="https://linkedin.com"><Linkedin size={18} /></Social>
              <Social href="https://twitter.com"><Twitter size={18} /></Social>
              <Social href="https://instagram.com"><Instagram size={18} /></Social>
              <Social href="https://github.com"><Github size={18} /></Social>
            </div>

            {/* WORKING CTA */}
            <Link
              to="/contact"
              className="btn btn-primary btn-lg"
              style={{ marginTop: '1rem', width: '100%', textAlign: 'center' }}
            >
              Start Free Trial
            </Link>
          </div>
        </div>

        {/* ================= MAP ================= */}
        <div style={mapWrapper}>
          <iframe
            title="SkyReti Location"
            src="https://www.google.com/maps?q=India&output=embed"
            style={map}
            loading="lazy"
          />
        </div>

        {/* ================= LEGAL ================= */}
        <div style={bottom}>
          <div style={legalLinks}>
            <Link to="/privacy" style={link}>Privacy Policy</Link>
            <span>•</span>
            <Link to="/terms" style={link}>Terms & Conditions</Link>
            <span>•</span>
            <Link to="/contact" style={link}>Contact</Link>
          </div>

          <p style={copyright}>
            © {new Date().getFullYear()} SkyReti Educore. All rights reserved.
          </p>
        </div>

      </div>
    </footer>
  );
};

/* ================= SUB COMPONENT ================= */
const Social = ({ href, children }) => (
  <a
    href={href}
    target="_blank"
    rel="noreferrer"
    style={socialIcon}
  >
    {children}
  </a>
);

/* ================= STYLES ================= */
const footer = {
  background: 'var(--glass-bg)',
  backdropFilter: 'var(--glass-blur)',
  borderTop: '1px solid var(--glass-border)',
  padding: '3rem 0',
  marginTop: '4rem'
};

const grid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
  gap: '2rem'
};

const title = {
  color: 'var(--text-primary)',
  marginBottom: '1rem'
};

const text = {
  color: 'var(--text-secondary)',
  lineHeight: 1.6
};

const ul = {
  listStyle: 'none',
  padding: 0
};

const link = {
  color: 'var(--text-secondary)',
  textDecoration: 'none'
};

const contactItem = {
  display: 'flex',
  alignItems: 'center',
  gap: '0.5rem',
  marginBottom: '0.75rem',
  color: 'var(--text-secondary)'
};

const socialRow = {
  display: 'flex',
  gap: '0.75rem',
  flexWrap: 'wrap'
};

const socialIcon = {
  color: 'var(--text-secondary)',
  border: '1px solid var(--glass-border)',
  padding: '0.5rem',
  borderRadius: '0.5rem',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  transition: 'all 0.3s ease'
};

const mapWrapper = {
  marginTop: '2.5rem',
  borderRadius: '1rem',
  overflow: 'hidden',
  border: '1px solid var(--glass-border)'
};

const map = {
  width: '100%',
  height: '160px',
  border: 0,
  filter: 'grayscale(30%)'
};

const bottom = {
  marginTop: '2rem',
  paddingTop: '1.5rem',
  borderTop: '1px solid var(--glass-border)',
  textAlign: 'center'
};

const legalLinks = {
  display: 'flex',
  justifyContent: 'center',
  gap: '0.75rem',
  marginBottom: '0.75rem'
};

const copyright = {
  color: 'var(--text-tertiary)',
  fontSize: '0.9rem'
};

export default Footer;
