// src/components/Navbar.jsx
import React, { useState } from 'react';
import '..//styles/index.css';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  Home, 
  Grid, 
  Zap, 
  DollarSign, 
  BookOpen, 
  LogIn,
  Menu,
  X,
  ChevronDown
} from 'lucide-react';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSolutionsOpen, setIsSolutionsOpen] = useState(false);

  const navItems = [
    { name: 'Home', path: '/', icon: <Home size={18} /> },
    { 
      name: 'Solutions', 
      path: '#',
      icon: <Grid size={18} />,
      dropdown: [
        { name: 'For Schools', path: '/solutions/schools' },
        { name: 'For Universities', path: '/solutions/universities' },
        { name: 'For Libraries', path: '/solutions/libraries' },
        { name: 'For HR Teams', path: '/solutions/hr' },
        { name: 'For Startups', path: '/solutions/startups' },
        { name: 'For AI Labs', path: '/solutions/ai' },
        { name: 'For Influencers', path: '/solutions/influencers' },
        { name: 'For Entrepreneurs', path: '/solutions/entrepreneurs' },
      ]
    },
    { name: 'Features', path: '/features', icon: <Zap size={18} /> },
    { name: 'Pricing', path: '/pricing', icon: <DollarSign size={18} /> },
    { name: 'Resources', path: '/resources', icon: <BookOpen size={18} /> },
    { name: 'Dashboard', path: '/dashboard', icon: <Zap size={18} />, highlight: true },
    {name: 'Fact Check' , path:'/Factcheck' , icon:<Zap size={13}/>}
  ];

  const handleNavigation = (path) => {
    if (path === '/dashboard') {
      navigate('/dashboard');
    } else if (path.startsWith('/solutions/')) {
      // For demo, show alert. You can create actual pages later
      alert(`Navigating to ${path.split('/').pop().toUpperCase()} page (Page under development)`);
      navigate(path);
    } else if (path === '/features') {
      alert('Features page coming soon!');
    } else if (path === '/pricing') {
      alert('Pricing page coming soon!');
    } else if (path === '/resources') {
      alert('Resources page coming soon!');
    } else {
      navigate(path);
    }
    setIsMenuOpen(false);
    setIsSolutionsOpen(false);
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-gray-900/90 backdrop-blur-lg border-b border-gray-800">
      <div className="container mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-xl flex items-center justify-center">
              <span className="text-white font-bold text-xl">SE</span>
            </div>
            <div>
              <span className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text text-transparent">
                SkyReti Educore
              </span>
              <div className="text-xs text-gray-400">Premium Platform</div>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-1">
            {navItems.map((item) => (
              <div key={item.name} className="relative group">
                {item.dropdown ? (
                  <button
                    onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
                      location.pathname === item.path
                        ? 'bg-gray-800 text-white'
                        : 'text-gray-300 hover:bg-gray-800 hover:text-white'
                    }`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                    <ChevronDown size={16} className={`transition-transform ${isSolutionsOpen ? 'rotate-180' : ''}`} />
                  </button>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
                      location.pathname === item.path
                        ? 'bg-gray-800 text-white'
                        : 'text-gray-300 hover:bg-gray-800 hover:text-white'
                    } ${item.highlight ? 'bg-gradient-to-r from-cyan-600 to-blue-700 hover:from-cyan-700 hover:to-blue-800' : ''}`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}

                {/* Dropdown Menu */}
                {item.dropdown && isSolutionsOpen && (
                  <div className="absolute top-full left-0 mt-2 w-64 bg-gray-900 border border-gray-800 rounded-xl shadow-2xl overflow-hidden">
                    {item.dropdown.map((subItem) => (
                      <button
                        key={subItem.name}
                        onClick={() => handleNavigation(subItem.path)}
                        className="w-full text-left px-4 py-3 hover:bg-gray-800 text-gray-300 hover:text-white flex items-center space-x-3 transition-colors"
                      >
                        <div className="w-2 h-2 bg-cyan-500 rounded-full"></div>
                        <span>{subItem.name}</span>
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ))}

            {/* Auth Buttons */}
            <div className="flex items-center space-x-3 ml-4">
              <button
                onClick={() => {
                  alert('Login functionality coming soon!');
                  navigate('/login');
                }}
                className="flex items-center space-x-2 px-4 py-2 text-gray-300 hover:text-white transition-colors"
              >
                <LogIn size={18} />
                <span>Sign In</span>
              </button>
              <button
                onClick={() => {
                  alert('Starting your free trial! Redirecting to signup...');
                  navigate('/signup');
                }}
                className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 px-6 py-2.5 rounded-xl font-semibold transition-all hover:scale-105"
              >
                Get Started Free
              </button>
            </div>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden text-gray-300 hover:text-white"
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 pb-4 border-t border-gray-800 pt-4">
            {navItems.map((item) => (
              <div key={item.name}>
                {item.dropdown ? (
                  <div className="mb-2">
                    <button
                      onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                      className="flex items-center justify-between w-full px-4 py-3 text-gray-300 hover:text-white hover:bg-gray-800 rounded-lg transition-colors"
                    >
                      <div className="flex items-center space-x-3">
                        {item.icon}
                        <span>{item.name}</span>
                      </div>
                      <ChevronDown size={16} className={`transition-transform ${isSolutionsOpen ? 'rotate-180' : ''}`} />
                    </button>
                    {isSolutionsOpen && (
                      <div className="ml-8 mt-2 space-y-2">
                        {item.dropdown.map((subItem) => (
                          <button
                            key={subItem.name}
                            onClick={() => handleNavigation(subItem.path)}
                            className="flex items-center space-x-3 w-full px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg transition-colors"
                          >
                            <div className="w-2 h-2 bg-cyan-500 rounded-full"></div>
                            <span>{subItem.name}</span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    className={`flex items-center space-x-3 w-full px-4 py-3 rounded-lg transition-colors mb-2 ${
                      location.pathname === item.path
                        ? 'bg-gray-800 text-white'
                        : 'text-gray-300 hover:bg-gray-800 hover:text-white'
                    } ${item.highlight ? 'bg-gradient-to-r from-cyan-600 to-blue-700' : ''}`}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}
              </div>
            ))}

            <div className="pt-4 border-t border-gray-800 mt-4">
              <button
                onClick={() => {
                  alert('Login functionality coming soon!');
                  setIsMenuOpen(false);
                }}
                className="flex items-center justify-center space-x-2 w-full px-4 py-3 text-gray-300 hover:text-white hover:bg-gray-800 rounded-lg transition-colors mb-2"
              >
                <LogIn size={18} />
                <span>Sign In</span>
              </button>
              <button
                onClick={() => {
                  alert('Starting your free trial!');
                  setIsMenuOpen(false);
                }}
                className="bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 w-full px-4 py-3 rounded-xl font-semibold transition-all"
              >
                Get Started Free
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
