import React, { useState } from 'react';
import Layout from '../../components/Layout';
import {
  Check,
  Smartphone,
  Globe,
  MessageCircle,
  CreditCard,
  Users,
  BarChart,
  BookOpen,
  Award,
  Calendar
} from 'lucide-react';

const UniversitySolution = () => {
  const [selectedPackage, setSelectedPackage] = useState('enterprise');
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [showContactForm, setShowContactForm] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    universityName: '',
    message: ''
  });

  /* ================================
     UNIVERSITY OFFERINGS
  ================================= */
  const offerings = {
    website: {
      title: 'University Website & Portal',
      icon: <Globe size={24} />,
      price: '$3,999',
      features: [
        'University Website with CMS',
        'Admissions & Online Applications',
        'Departments, Faculties & Programs',
        'Research Publications & Journals',
        'Events, Notices & Announcements',
        'SEO Optimized & Mobile Responsive',
        'Student & Faculty Login Portal'
      ]
    },
    studentApp: {
      title: 'Student Mobile App',
      icon: <Smartphone size={24} />,
      price: '$5,999',
      features: [
        'Attendance & Academic Tracking',
        'Exam Results & Transcripts',
        'LMS â€“ Notes, Videos, Assignments',
        'Fee Payment & Invoices',
        'Timetable & Academic Calendar',
        'Library & Digital Resources',
        'Push Notifications'
      ]
    },
    facultyApp: {
      title: 'Faculty Management App',
      icon: <Users size={24} />,
      price: '$4,499',
      features: [
        'Course & Subject Management',
        'Attendance & Evaluation',
        'Research & Publication Tracking',
        'Student Performance Analytics',
        'Faculty Payroll & Leave',
        'Internal Communication Tools'
      ]
    },
    erp: {
      title: 'University ERP & Admin Panel',
      icon: <BarChart size={24} />,
      price: '$7,999',
      features: [
        'Complete University ERP',
        'Multi-Campus Management',
        'Finance, HR & Payroll',
        'Hostel & Transport System',
        'Accreditation (NAAC / NIRF) Reports',
        'Custom Analytics Dashboard'
      ]
    }
  };

  /* ================================
     PRICING PACKAGES
  ================================= */
  const packages = {
    basic: {
      name: 'Basic Digital Presence',
      price: '$3,999',
      includes: ['University Website'],
      bestFor: 'Small colleges & institutes'
    },
    academic: {
      name: 'Academic Suite',
      price: '$8,999',
      includes: ['Website', 'Student App', 'Faculty App'],
      bestFor: 'Teaching-focused universities',
      savings: 'Save $2,499'
    },
    enterprise: {
      name: 'Enterprise University Suite',
      price: '$14,999',
      includes: ['Website', 'Student App', 'Faculty App', 'ERP Panel'],
      bestFor: 'Large universities & multi-campus institutes',
      savings: 'Save $5,000'
    }
  };

  const handleChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  return (
    <Layout>

      {/* ================= HERO ================= */}
      <div style={{
        background: 'linear-gradient(135deg, #1e40af, #1d4ed8)',
        color: 'white',
        padding: '5rem 2rem',
        textAlign: 'center'
      }}>
        <div style={{ fontSize: '4rem' }}>ðŸŽ“</div>
        <h1 style={{ fontSize: '3.5rem', fontWeight: '800' }}>
          University Digital Transformation
        </h1>
        <p style={{ maxWidth: '900px', margin: '1.5rem auto', fontSize: '1.2rem', opacity: 0.9 }}>
          Smart ERP, LMS, Mobile Apps & Websites designed for modern universities,
          colleges and higher education institutions.
        </p>

        <div style={{ display: 'flex', justifyContent: 'center', gap: '1.5rem', flexWrap: 'wrap' }}>
          <button
            onClick={() => setShowBookingForm(true)}
            style={ctaPrimary}
          >
            <CreditCard size={18} /> Book & Pay
          </button>
          <button
            onClick={() => setShowContactForm(true)}
            style={ctaSecondary}
          >
            <MessageCircle size={18} /> Contact for Demo
          </button>
        </div>
      </div>

      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '3rem 2rem' }}>

        {/* ================= OFFERINGS ================= */}
        <section>
          <h2 style={sectionTitle}>University-Level Solutions</h2>
          <div style={grid}>
            {Object.values(offerings).map((item, i) => (
              <div key={i} style={card}>
                <div style={iconBox}>{item.icon}</div>
                <h3>{item.title}</h3>
                <p style={{ fontWeight: 'bold', color: '#1d4ed8' }}>{item.price}</p>
                <ul style={list}>
                  {item.features.map((f, idx) => (
                    <li key={idx}><Check size={16} color="#10b981" /> {f}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>

        {/* ================= PACKAGES ================= */}
        <section style={{ marginTop: '5rem' }}>
          <h2 style={sectionTitle}>Pricing Packages</h2>
          <div style={grid}>
            {Object.entries(packages).map(([key, pkg]) => (
              <div
                key={key}
                onClick={() => setSelectedPackage(key)}
                style={{
                  ...card,
                  border: selectedPackage === key ? '3px solid #2563eb' : '1px solid #e5e7eb'
                }}
              >
                {pkg.savings && <div style={badge}>{pkg.savings}</div>}
                <h3>{pkg.name}</h3>
                <div style={{ fontSize: '2rem', fontWeight: '800' }}>{pkg.price}</div>
                <ul style={list}>
                  {pkg.includes.map((i, x) => (
                    <li key={x}><Check size={16} color="#10b981" /> {i}</li>
                  ))}
                </ul>
                <p style={{ fontSize: '0.9rem', opacity: 0.7 }}>
                  Best for: {pkg.bestFor}
                </p>
              </div>
            ))}
          </div>
        </section>

        {/* ================= FINAL CTA ================= */}
        <div style={{
          marginTop: '5rem',
          background: 'linear-gradient(135deg, #1e3a8a, #1d4ed8)',
          color: 'white',
          padding: '4rem',
          borderRadius: '2rem',
          textAlign: 'center'
        }}>
          <h2 style={{ fontSize: '2.5rem' }}>Upgrade Your University Digitally</h2>
          <p style={{ maxWidth: '700px', margin: '1rem auto', opacity: 0.9 }}>
            Trusted by universities for ERP, LMS, admissions, research & analytics.
          </p>
          <button onClick={() => setShowContactForm(true)} style={ctaPrimary}>
            Schedule Free Consultation
          </button>
        </div>
      </div>
    </Layout>
  );
};

/* ================================
   STYLES
================================= */
const sectionTitle = {
  textAlign: 'center',
  fontSize: '2.8rem',
  marginBottom: '3rem',
  color: '#1e3a8a'
};

const grid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
  gap: '2rem'
};

const card = {
  background: '#fff',
  padding: '2.5rem',
  borderRadius: '1.5rem',
  boxShadow: '0 20px 40px rgba(0,0,0,0.08)',
  cursor: 'pointer'
};

const iconBox = {
  background: '#1d4ed8',
  color: 'white',
  width: '60px',
  height: '60px',
  borderRadius: '1rem',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  marginBottom: '1rem'
};

const list = {
  listStyle: 'none',
  padding: 0,
  marginTop: '1rem'
};

const badge = {
  background: '#10b981',
  color: 'white',
  padding: '0.4rem 1rem',
  borderRadius: '1rem',
  fontSize: '0.85rem',
  marginBottom: '1rem',
  display: 'inline-block'
};

const ctaPrimary = {
  padding: '1rem 2.5rem',
  background: 'white',
  color: '#1d4ed8',
  border: 'none',
  borderRadius: '0.75rem',
  fontWeight: 'bold',
  cursor: 'pointer',
  display: 'inline-flex',
  gap: '0.5rem',
  alignItems: 'center'
};

const ctaSecondary = {
  ...ctaPrimary,
  background: 'transparent',
  color: 'white',
  border: '2px solid white'
};

export default UniversitySolution;
