// src/components/Navbar.jsx
import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  Home, 
  Grid, 
  Zap, 
  DollarSign, 
  BookOpen, 
  LogIn,
  Menu,
  X,
  ChevronDown
} from 'lucide-react';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSolutionsOpen, setIsSolutionsOpen] = useState(false);

  const navItems = [
    { name: 'Home', path: '/', icon: <Home size={18} /> },
    { 
      name: 'Solutions', 
      path: '#',
      icon: <Grid size={18} />,
      dropdown: [
        { name: 'For Schools', path: '/solutions/schools' },
        { name: 'For Universities', path: '/solutions/universities' },
        { name: 'For Libraries', path: '/solutions/libraries' },
        { name: 'For HR Teams', path: '/solutions/hrteams' },
        { name: 'For Startups', path: '/solutions/startups' },
        { name: 'For AI Labs', path: '/solutions/ailabs' },
        { name: 'For Influencers', path: '/solutions/influencers' },
        { name: 'For Entrepreneurs', path: '/solutions/entrepreneurs' },
      ]
    },
    { name: 'Features', path: '/features', icon: <Zap size={18} /> },
    { name: 'Pricing', path: '/pricing', icon: <DollarSign size={18} /> },
    { name: 'Resources', path: '/resources', icon: <BookOpen size={18} /> },
    { name: 'Dashboard', path: '/dashboard', icon: <Zap size={18} />, highlight: true },
    { name: 'Fact Check', path: '/factcheck', icon: <Zap size={18} /> }
  ];

  const handleNavigation = (path) => {
    if (path.startsWith('/solutions/')) {
      // Extract page name from path
      const pageName = path.split('/').pop().replace(/([A-Z])/g, ' $1').trim();
      alert(`Navigating to ${pageName.toUpperCase()} page`);
    } else if (path === '/features') {
      alert('Features page coming soon!');
    } else if (path === '/pricing') {
      alert('Pricing page coming soon!');
    } else if (path === '/resources') {
      alert('Resources page coming soon!');
    } else if (path === '/factcheck') {
      alert('Fact Check page coming soon!');
    }
    
    navigate(path);
    setIsMenuOpen(false);
    setIsSolutionsOpen(false);
  };

  // Inline styles object
  const styles = {
    navbar: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      zIndex: 50,
      backgroundColor: 'rgba(17, 24, 39, 0.9)',
      backdropFilter: 'blur(10px)',
      borderBottom: '1px solid rgba(31, 41, 55, 1)'
    },
    container: {
      maxWidth: '1280px',
      margin: '0 auto',
      padding: '1rem 1.5rem'
    },
    content: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    logoLink: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      textDecoration: 'none'
    },
    logoIcon: {
      width: '2.5rem',
      height: '2.5rem',
      background: 'linear-gradient(to right, #06b6d4, #2563eb)',
      borderRadius: '0.75rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    logoText: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      background: 'linear-gradient(to right, #22d3ee, #60a5fa)',
      WebkitBackgroundClip: 'text',
      backgroundClip: 'text',
      color: 'transparent'
    },
    logoSubtext: {
      fontSize: '0.75rem',
      color: '#9ca3af'
    },
    desktopNav: {
      display: 'none',
      alignItems: 'center',
      gap: '0.25rem'
    },
    '@media (min-width: 768px)': {
      desktopNav: {
        display: 'flex'
      }
    },
    navItem: {
      position: 'relative'
    },
    navButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      transition: 'all 0.2s',
      border: 'none',
      cursor: 'pointer',
      fontSize: '0.875rem'
    },
    navButtonDefault: {
      backgroundColor: 'transparent',
      color: '#d1d5db'
    },
    navButtonActive: {
      backgroundColor: 'rgba(31, 41, 55, 1)',
      color: 'white'
    },
    navButtonHover: {
      backgroundColor: 'rgba(31, 41, 55, 1)',
      color: 'white'
    },
    navButtonHighlight: {
      background: 'linear-gradient(to right, #0891b2, #1d4ed8)',
      color: 'white'
    },
    dropdown: {
      position: 'absolute',
      top: '100%',
      left: 0,
      marginTop: '0.5rem',
      width: '16rem',
      backgroundColor: 'rgba(17, 24, 39, 1)',
      border: '1px solid rgba(31, 41, 55, 1)',
      borderRadius: '0.75rem',
      boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)',
      overflow: 'hidden'
    },
    dropdownItem: {
      width: '100%',
      textAlign: 'left',
      padding: '0.75rem 1rem',
      backgroundColor: 'transparent',
      color: '#d1d5db',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'background-color 0.2s'
    },
    dropdownItemHover: {
      backgroundColor: 'rgba(31, 41, 55, 1)',
      color: 'white'
    },
    dropdownIndicator: {
      width: '0.5rem',
      height: '0.5rem',
      backgroundColor: '#06b6d4',
      borderRadius: '50%'
    },
    authButtons: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      marginLeft: '1rem'
    },
    signInButton: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      padding: '0.5rem 1rem',
      backgroundColor: 'transparent',
      color: '#d1d5db',
      border: 'none',
      cursor: 'pointer',
      fontSize: '0.875rem',
      transition: 'color 0.2s'
    },
    getStartedButton: {
      background: 'linear-gradient(to right, #10b981, #059669)',
      padding: '0.625rem 1.5rem',
      borderRadius: '0.75rem',
      fontWeight: '600',
      border: 'none',
      cursor: 'pointer',
      color: 'white',
      fontSize: '0.875rem',
      transition: 'all 0.2s'
    },
    mobileMenuButton: {
      display: 'block',
      backgroundColor: 'transparent',
      border: 'none',
      color: '#d1d5db',
      cursor: 'pointer'
    },
    '@media (min-width: 768px)': {
      mobileMenuButton: {
        display: 'none'
      }
    },
    mobileMenu: {
      marginTop: '1rem',
      paddingBottom: '1rem',
      borderTop: '1px solid rgba(31, 41, 55, 1)',
      paddingTop: '1rem'
    },
    mobileMenuItem: {
      marginBottom: '0.5rem'
    },
    mobileMenuDropdown: {
      marginLeft: '2rem',
      marginTop: '0.5rem',
      display: 'flex',
      flexDirection: 'column',
      gap: '0.5rem'
    }
  };

  // Helper function to merge styles
  const mergeStyles = (...styleObjects) => {
    return Object.assign({}, ...styleObjects);
  };

  // Check if current path is active
  const isActive = (path) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.container}>
        <div style={styles.content}>
          {/* Logo */}
          <Link to="/" style={styles.logoLink}>
            <div style={styles.logoIcon}>
              <span style={{color: 'white', fontWeight: 'bold', fontSize: '1.25rem'}}>SE</span>
            </div>
            <div>
              <div style={styles.logoText}>SkyReti Educore</div>
              <div style={styles.logoSubtext}>Premium Platform</div>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div style={mergeStyles(styles.desktopNav, styles['@media (min-width: 768px)'].desktopNav)}>
            {navItems.map((item) => (
              <div key={item.name} style={styles.navItem}>
                {item.dropdown ? (
                  <button
                    onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                    style={mergeStyles(
                      styles.navButton,
                      styles.navButtonDefault,
                      isActive(item.path) && styles.navButtonActive,
                      {':hover': styles.navButtonHover}
                    )}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundColor = styles.navButtonHover.backgroundColor;
                      e.currentTarget.style.color = styles.navButtonHover.color;
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundColor = isActive(item.path) 
                        ? styles.navButtonActive.backgroundColor 
                        : styles.navButtonDefault.backgroundColor;
                      e.currentTarget.style.color = isActive(item.path)
                        ? styles.navButtonActive.color
                        : styles.navButtonDefault.color;
                    }}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                    <ChevronDown size={16} style={{
                      transition: 'transform 0.2s',
                      transform: isSolutionsOpen ? 'rotate(180deg)' : 'rotate(0deg)'
                    }} />
                  </button>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    style={mergeStyles(
                      styles.navButton,
                      item.highlight ? styles.navButtonHighlight : styles.navButtonDefault,
                      isActive(item.path) && !item.highlight && styles.navButtonActive,
                      {':hover': item.highlight ? {} : styles.navButtonHover}
                    )}
                    onMouseEnter={(e) => {
                      if (!item.highlight) {
                        e.currentTarget.style.backgroundColor = styles.navButtonHover.backgroundColor;
                        e.currentTarget.style.color = styles.navButtonHover.color;
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (!item.highlight) {
                        e.currentTarget.style.backgroundColor = isActive(item.path) 
                          ? styles.navButtonActive.backgroundColor 
                          : styles.navButtonDefault.backgroundColor;
                        e.currentTarget.style.color = isActive(item.path)
                          ? styles.navButtonActive.color
                          : styles.navButtonDefault.color;
                      }
                    }}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}

                {/* Dropdown Menu */}
                {item.dropdown && isSolutionsOpen && (
                  <div style={styles.dropdown}>
                    {item.dropdown.map((subItem) => (
                      <button
                        key={subItem.name}
                        onClick={() => handleNavigation(subItem.path)}
                        style={styles.dropdownItem}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.backgroundColor = styles.dropdownItemHover.backgroundColor;
                          e.currentTarget.style.color = styles.dropdownItemHover.color;
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.backgroundColor = styles.dropdownItem.backgroundColor;
                          e.currentTarget.style.color = styles.dropdownItem.color;
                        }}
                      >
                        <div style={styles.dropdownIndicator}></div>
                        <span>{subItem.name}</span>
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ))}

            {/* Auth Buttons */}
            <div style={styles.authButtons}>
              <button
                onClick={() => {
                  alert('Login functionality coming soon!');
                  navigate('/login');
                }}
                style={styles.signInButton}
                onMouseEnter={(e) => {
                  e.currentTarget.style.color = 'white';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.color = styles.signInButton.color;
                }}
              >
                <LogIn size={18} />
                <span>Sign In</span>
              </button>
              <button
                onClick={() => {
                  alert('Starting your free trial! Redirecting to signup...');
                  navigate('/signup');
                }}
                style={styles.getStartedButton}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = 'linear-gradient(to right, #0da271, #047857)';
                  e.currentTarget.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = styles.getStartedButton.background;
                  e.currentTarget.style.transform = 'scale(1)';
                }}
              >
                Get Started Free
              </button>
            </div>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            style={mergeStyles(styles.mobileMenuButton, styles['@media (min-width: 768px)'].mobileMenuButton)}
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div style={styles.mobileMenu}>
            {navItems.map((item) => (
              <div key={item.name} style={styles.mobileMenuItem}>
                {item.dropdown ? (
                  <div>
                    <button
                      onClick={() => setIsSolutionsOpen(!isSolutionsOpen)}
                      style={{
                        ...styles.dropdownItem,
                        width: '100%',
                        justifyContent: 'space-between'
                      }}
                    >
                      <div style={{display: 'flex', alignItems: 'center', gap: '0.75rem'}}>
                        {item.icon}
                        <span>{item.name}</span>
                      </div>
                      <ChevronDown size={16} style={{
                        transition: 'transform 0.2s',
                        transform: isSolutionsOpen ? 'rotate(180deg)' : 'rotate(0deg)'
                      }} />
                    </button>
                    {isSolutionsOpen && (
                      <div style={styles.mobileMenuDropdown}>
                        {item.dropdown.map((subItem) => (
                          <button
                            key={subItem.name}
                            onClick={() => handleNavigation(subItem.path)}
                            style={{
                              ...styles.dropdownItem,
                              justifyContent: 'flex-start'
                            }}
                          >
                            <div style={styles.dropdownIndicator}></div>
                            <span>{subItem.name}</span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <button
                    onClick={() => handleNavigation(item.path)}
                    style={mergeStyles(
                      styles.dropdownItem,
                      item.highlight && styles.navButtonHighlight,
                      isActive(item.path) && !item.highlight && {backgroundColor: 'rgba(31, 41, 55, 1)', color: 'white'}
                    )}
                  >
                    {item.icon}
                    <span>{item.name}</span>
                  </button>
                )}
              </div>
            ))}

            <div style={{
              paddingTop: '1rem',
              marginTop: '1rem',
              borderTop: '1px solid rgba(31, 41, 55, 1)'
            }}>
              <button
                onClick={() => {
                  alert('Login functionality coming soon!');
                  setIsMenuOpen(false);
                }}
                style={{
                  ...styles.dropdownItem,
                  justifyContent: 'center',
                  marginBottom: '0.5rem'
                }}
              >
                <LogIn size={18} />
                <span>Sign In</span>
              </button>
              <button
                onClick={() => {
                  alert('Starting your free trial!');
                  setIsMenuOpen(false);
                }}
                style={{
                  ...styles.getStartedButton,
                  width: '100%'
                }}
              >
                Get Started Free
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
