// src/pages/solutions/Universities.jsx
import React from 'react';
import Layout from '../../components/Layout';
import { 
  GraduationCap,
  Users,
  BookOpen,
  BarChart,
  Globe,
  Database,
  FileText,
  Award,
  Target,
  Book,
  Brain,
  Network
} from 'lucide-react';

const UniversitiesSolution = () => {
  const modules = [
    {
      icon: <GraduationCap size={28} />,
      title: "Academic Management",
      features: ["Course Catalog", "Credit Systems", "Degree Audit", "Transcripts"]
    },
    {
      icon: <Brain size={28} />,
      title: "Research Portal",
      features: ["Grant Management", "Publications", "Collaborations", "Labs"]
    },
    {
      icon: <Users size={28} />,
      title: "Faculty System",
      features: ["Workload", "Research", "Publications", "Reviews"]
    },
    {
      icon: <Globe size={28} />,
      title: "International Office",
      features: ["Visa Tracking", "Language Support", "Cultural Integration"]
    },
    {
      icon: <Network size={28} />,
      title: "Alumni Network",
      features: ["Career Services", "Mentorship", "Donations", "Events"]
    },
    {
      icon: <Target size={28} />,
      title: "Accreditation",
      features: ["Compliance", "Reports", "Audits", "Standards"]
    }
  ];

  return (
    <Layout>
      {/* Hero Section */}
      <div className="solution-hero" style={{
        background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
        color: 'white',
        padding: '5rem 2rem',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: '-50px',
          right: '-50px',
          width: '200px',
          height: '200px',
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '50%'
        }}></div>
        
        <div className="solution-icon" style={{
          fontSize: '5rem',
          marginBottom: '1.5rem'
        }}>
          ðŸŽ“
        </div>
        <h1 style={{ 
          fontSize: '3.5rem', 
          marginBottom: '1rem',
          fontWeight: '800'
        }}>
          University Excellence Platform
        </h1>
        <p style={{ 
          fontSize: '1.3rem', 
          maxWidth: '800px', 
          margin: '0 auto 2rem',
          opacity: 0.9,
          lineHeight: '1.6'
        }}>
          Comprehensive platform for higher education institutions combining academic management, 
          research excellence, and global collaboration tools.
        </p>
      </div>

      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
        {/* Modules Grid */}
        <section style={{ margin: '5rem 0' }}>
          <h2 style={{ 
            textAlign: 'center', 
            marginBottom: '3rem', 
            fontSize: '2.8rem',
            background: 'linear-gradient(135deg, #10b981, #059669)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            Comprehensive University Modules
          </h2>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
            gap: '2rem'
          }}>
            {modules.map((module, index) => (
              <div key={index} style={{
                background: 'linear-gradient(145deg, #ffffff, #f0fdf4)',
                padding: '2.5rem',
                borderRadius: '1.5rem',
                boxShadow: '0 20px 40px rgba(16, 185, 129, 0.1)',
                border: '1px solid #d1fae5',
                position: 'relative',
                overflow: 'hidden'
              }}>
                <div style={{
                  background: 'linear-gradient(135deg, #10b981, #059669)',
                  width: '70px',
                  height: '70px',
                  borderRadius: '1rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  marginBottom: '1.5rem',
                  color: 'white'
                }}>
                  {module.icon}
                </div>
                <h3 style={{ 
                  marginBottom: '1.5rem', 
                  fontSize: '1.5rem',
                  color: '#065f46'
                }}>
                  {module.title}
                </h3>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
                  {module.features.map((feature, idx) => (
                    <span key={idx} style={{
                      padding: '0.5rem 1rem',
                      background: 'rgba(16, 185, 129, 0.1)',
                      color: '#065f46',
                      borderRadius: '2rem',
                      fontSize: '0.9rem',
                      fontWeight: '500'
                    }}>
                      {feature}
                    </span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* CTA */}
        <div style={{
          background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
          color: 'white',
          padding: '4rem',
          borderRadius: '2rem',
          textAlign: 'center',
          marginTop: '4rem'
        }}>
          <h2 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>
            Elevate Your University
          </h2>
          <p style={{ fontSize: '1.1rem', marginBottom: '2rem' }}>
            Join Ivy League institutions using our platform
          </p>
          <button style={{
            padding: '1rem 3rem',
            background: 'white',
            color: '#10b981',
            border: 'none',
            borderRadius: '0.5rem',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            cursor: 'pointer'
          }}>
            Request University Demo
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default UniversitiesSolution;
