// src/pages/Pricing.jsx
import React, { useState } from 'react';
import { Check, X, HelpCircle } from 'lucide-react';
import Footer from '../components/Footer';
import '../Styles/home.css';
const Pricing = () => {
  const [billingCycle, setBillingCycle] = useState('annual'); // 'annual' or 'monthly'

  const plans = [
    {
      name: 'Starter',
      description: 'Perfect for small schools & startups',
      monthlyPrice: '$49',
      annualPrice: '$39',
      features: [
        'Up to 100 users',
        'Basic analytics',
        'Email support',
        '5GB storage',
        'Core modules only'
      ],
      excluded: [
        'AI features',
        'Advanced analytics',
        'Phone support',
        'Custom integrations'
      ],
      popular: false
    },
    {
      name: 'Professional',
      description: 'For growing institutions & businesses',
      monthlyPrice: '$149',
      annualPrice: '$119',
      features: [
        'Up to 500 users',
        'Advanced analytics',
        'Priority support',
        '100GB storage',
        'All core modules',
        'Basic AI features',
        'API access'
      ],
      excluded: [
        'Enterprise security',
        'Custom development',
        'Dedicated account manager'
      ],
      popular: true
    },
    {
      name: 'Enterprise',
      description: 'For large organizations & universities',
      monthlyPrice: 'Custom',
      annualPrice: 'Custom',
      features: [
        'Unlimited users',
        'Premium AI features',
        '24/7 phone support',
        'Unlimited storage',
        'All modules',
        'Custom integrations',
        'Enterprise security',
        'Dedicated account manager',
        'SLA guarantee'
      ],
      excluded: [],
      popular: false
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-black text-white">
      {/* Hero Section */}
      <section className="pt-32 pb-20">
        <div className="container mx-auto px-6 text-center">
          <h1 className="text-5xl md:text-7xl font-bold mb-6">
            Simple, Transparent{' '}
            <span className="bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
              Pricing
            </span>
          </h1>
          
          <p className="text-xl text-gray-300 max-w-3xl mx-auto mb-12">
            Choose the perfect plan for your institution. All plans include core features with no hidden fees.
          </p>

          {/* Billing Toggle */}
          <div className="inline-flex items-center bg-gray-800 rounded-full p-1 mb-12">
            <button
              onClick={() => setBillingCycle('monthly')}
              className={`px-6 py-3 rounded-full transition-all ${
                billingCycle === 'monthly'
                  ? 'bg-cyan-600 text-white'
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              Monthly
            </button>
            <button
              onClick={() => setBillingCycle('annual')}
              className={`px-6 py-3 rounded-full transition-all ${
                billingCycle === 'annual'
                  ? 'bg-cyan-600 text-white'
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              Annual <span className="text-green-400 ml-1">(Save 20%)</span>
            </button>
          </div>
        </div>
      </section>

      {/* Pricing Cards */}
      <section className="py-20">
        <div className="container mx-auto px-6">
          <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
            {plans.map((plan, idx) => (
              <div
                key={idx}
                className={`relative rounded-3xl p-8 ${
                  plan.popular
                    ? 'bg-gradient-to-b from-gray-800 to-gray-900 border-2 border-cyan-500'
                    : 'bg-gray-800/30 border border-gray-700'
                }`}
              >
                {plan.popular && (
                  <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 bg-gradient-to-r from-cyan-500 to-blue-600 text-white px-6 py-2 rounded-full text-sm font-bold">
                    MOST POPULAR
                  </div>
                )}
                
                <div className="text-center mb-8">
                  <h3 className="text-2xl font-bold mb-2">{plan.name}</h3>
                  <p className="text-gray-400 mb-6">{plan.description}</p>
                  
                  <div className="mb-4">
                    <span className="text-5xl font-bold">
                      {billingCycle === 'annual' ? plan.annualPrice : plan.monthlyPrice}
                    </span>
                    {plan.monthlyPrice !== 'Custom' && (
                      <span className="text-gray-400 ml-2">
                        /{billingCycle === 'annual' ? 'year' : 'month'}
                      </span>
                    )}
                  </div>
                  
                  <button
                    onClick={() => window.location.href = '/signup'}
                    className={`w-full py-4 rounded-xl font-bold text-lg ${
                      plan.popular
                        ? 'bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700'
                        : 'bg-gray-700 hover:bg-gray-600'
                    }`}
                  >
                    Get Started
                  </button>
                </div>

                {/* Features */}
                <div className="space-y-4">
                  <h4 className="font-bold text-lg">Includes:</h4>
                  {plan.features.map((feature, i) => (
                    <div key={i} className="flex items-center">
                      <Check className="w-5 h-5 text-green-400 mr-3 flex-shrink-0" />
                      <span className="text-gray-300">{feature}</span>
                    </div>
                  ))}
                  
                  {plan.excluded.length > 0 && (
                    <>
                      <h4 className="font-bold text-lg mt-6">Not included:</h4>
                      {plan.excluded.map((feature, i) => (
                        <div key={i} className="flex items-center">
                          <X className="w-5 h-5 text-red-400 mr-3 flex-shrink-0" />
                          <span className="text-gray-500">{feature}</span>
                        </div>
                      ))}
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* FAQ Section */}
      <section className="py-20 bg-gradient-to-b from-black to-gray-900">
        <div className="container mx-auto px-6">
          <h2 className="text-4xl font-bold text-center mb-12">Frequently Asked Questions</h2>
          
          <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
            {[
              {
                question: 'Can I switch plans later?',
                answer: 'Yes, you can upgrade or downgrade your plan at any time. Changes take effect immediately.'
              },
              {
                question: 'Is there a free trial?',
                answer: 'Yes, we offer a 14-day free trial on all paid plans. No credit card required.'
              },
              {
                question: 'Do you offer discounts for non-profits?',
                answer: 'Yes, we offer special pricing for educational institutions and non-profit organizations.'
              },
              {
                question: 'What payment methods do you accept?',
                answer: 'We accept all major credit cards, PayPal, and wire transfers for enterprise plans.'
              },
              {
                question: 'Can I cancel anytime?',
                answer: 'Yes, you can cancel your subscription at any time. No long-term contracts.'
              },
              {
                question: 'Do you offer custom solutions?',
                answer: 'Yes, our enterprise plan includes custom development and integrations.'
              }
            ].map((faq, idx) => (
              <div key={idx} className="bg-gray-800/30 p-6 rounded-2xl">
                <div className="flex items-start mb-4">
                  <HelpCircle className="w-6 h-6 text-cyan-400 mr-3 mt-1 flex-shrink-0" />
                  <h3 className="text-lg font-bold">{faq.question}</h3>
                </div>
                <p className="text-gray-400">{faq.answer}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Final CTA */}
      <section className="py-20 text-center">
        <div className="container mx-auto px-6">
          <h2 className="text-4xl font-bold mb-6">Still have questions?</h2>
          <p className="text-gray-400 text-xl mb-10 max-w-2xl mx-auto">
            Contact our sales team for a personalized demo and pricing quote.
          </p>
          <div className="flex flex-col md:flex-row gap-4 justify-center">
            <button 
              onClick={() => window.location.href = '/contact'}
              className="bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 px-10 py-5 rounded-2xl text-xl font-bold"
            >
              Contact Sales
            </button>
            <button 
              onClick={() => window.location.href = '/signup'}
              className="bg-gray-800 hover:bg-gray-700 border border-gray-700 px-10 py-5 rounded-2xl text-xl font-bold"
            >
